import{_ as a,a as e}from"./app.d843d859.js";const i={},p=e('<h1 id="\u7B2C-5-\u7AE0-\u4ECB\u7ECD\u91CD\u6784\u540D\u5F55" tabindex="-1"><a class="header-anchor" href="#\u7B2C-5-\u7AE0-\u4ECB\u7ECD\u91CD\u6784\u540D\u5F55" aria-hidden="true">#</a> \u7B2C 5 \u7AE0 \u4ECB\u7ECD\u91CD\u6784\u540D\u5F55</h1><p>\u672C\u4E66\u5269\u4F59\u7684\u7BC7\u5E45\u662F\u4E00\u4EFD\u91CD\u6784\u7684\u540D\u5F55\u3002\u6700\u521D\u8FD9\u4E2A\u540D\u5F55\u53EA\u662F\u6211\u7684\u4E2A\u4EBA\u7B14\u8BB0\uFF0C\u6211\u7528\u5B83\u6765\u63D0\u793A\u81EA\u5DF1\u5982\u4F55\u4EE5\u5B89\u5168\u4E14\u9AD8\u6548\u7684\u65B9\u5F0F\u8FDB\u884C\u91CD\u6784\u3002\u7136\u540E\u6211\u4E0D\u65AD\u7CBE\u70BC\u8FD9\u4EFD\u540D\u5F55\uFF0C\u5BF9\u4E00\u4E9B\u91CD\u6784\u7684\u6DF1\u5165\u63A2\u7D22\u53C8\u5F15\u51FA\u4E86\u66F4\u591A\u7684\u91CD\u6784\u624B\u6CD5\u3002\u5BF9\u4E8E\u4E0D\u592A\u5E38\u7528\u7684\u91CD\u6784\u624B\u6CD5\uFF0C\u6211\u8FD8\u662F\u4F1A\u4E0D\u65AD\u53C2\u9605\u8FD9\u4EFD\u540D\u5F55\u3002</p><h2 id="_5-1-\u91CD\u6784\u7684\u8BB0\u5F55\u683C\u5F0F" tabindex="-1"><a class="header-anchor" href="#_5-1-\u91CD\u6784\u7684\u8BB0\u5F55\u683C\u5F0F" aria-hidden="true">#</a> 5.1 \u91CD\u6784\u7684\u8BB0\u5F55\u683C\u5F0F</h2><p>\u4ECB\u7ECD\u91CD\u6784\u65F6\uFF0C\u6211\u91C7\u7528\u4E00\u79CD\u6807\u51C6\u683C\u5F0F\u3002\u6BCF\u4E2A\u91CD\u6784\u624B\u6CD5\u90FD\u6709\u5982\u4E0B 5 \u4E2A\u90E8\u5206\u3002</p><ul><li>\u9996\u5148\u662F\u540D\u79F0\uFF08name\uFF09\u3002\u8981\u5EFA\u9020\u4E00\u4E2A\u91CD\u6784\u8BCD\u6C47\u8868\uFF0C\u540D\u79F0\u662F\u5F88\u91CD\u8981\u7684\u3002\u8FD9\u4E2A\u540D\u79F0\u4E5F\u5C31\u662F\u6211\u5C06\u5728\u672C\u4E66\u5176\u4ED6\u5730\u65B9\u4F7F\u7528\u7684\u540D\u79F0\u3002\u5982\u4ECA\u91CD\u6784\u7ECF\u5E38\u4F1A\u6709\u591A\u4E2A\u540D\u5B57\uFF0C\u6240\u4EE5\u6211\u4F1A\u540C\u65F6\u5217\u51FA\u5E38\u89C1\u7684\u522B\u540D\u3002</li><li>\u540D\u79F0\u4E4B\u540E\u662F\u4E00\u4E2A\u7B80\u5355\u7684\u901F\u5199\uFF08sketch\uFF09\u3002\u8FD9\u90E8\u5206\u53EF\u4EE5\u5E2E\u52A9\u4F60\u66F4\u5FEB\u627E\u5230\u4F60\u6240\u9700\u8981\u7684\u91CD\u6784\u624B\u6CD5\u3002</li><li>\u52A8\u673A\uFF08motivation\uFF09\u4E3A\u4F60\u4ECB\u7ECD\u201C\u4E3A\u4EC0\u4E48\u9700\u8981\u505A\u8FD9\u4E2A\u91CD\u6784\u201D\u548C\u201C\u4EC0\u4E48\u60C5\u51B5\u4E0B\u4E0D\u8BE5\u505A\u8FD9\u4E2A\u91CD\u6784\u201D\u3002</li><li>\u505A\u6CD5\uFF08mechanics\uFF09\u7B80\u660E\u627C\u8981\u5730\u4E00\u6B65\u4E00\u6B65\u4ECB\u7ECD\u5982\u4F55\u8FDB\u884C\u6B64\u91CD\u6784\u3002</li><li>\u8303\u4F8B\uFF08examples\uFF09\u4EE5\u4E00\u4E2A\u5341\u5206\u7B80\u5355\u7684\u4F8B\u5B50\u8BF4\u660E\u6B64\u91CD\u6784\u624B\u6CD5\u5982\u4F55\u8FD0\u4F5C\u3002</li></ul><p>\u901F\u5199\u90E8\u5206\u4F1A\u4EE5\u4EE3\u7801\u793A\u4F8B\u7684\u5F62\u5F0F\u5C55\u793A\u91CD\u6784\u5E26\u6765\u7684\u8F6C\u53D8\u3002\u901F\u5199\u7684\u7528\u610F\u4E0D\u662F\u89E3\u91CA\u91CD\u6784\u7684\u7528\u9014\uFF0C\u66F4\u4E0D\u662F\u8BE6\u7EC6\u8BB2\u89E3\u5982\u4F55\u64CD\u4F5C\u8FD9\u4E2A\u91CD\u6784\uFF1B\u4F46\u5982\u679C\u4F60\u66FE\u7ECF\u770B\u8FC7\u8FD9\u4E2A\u91CD\u6784\u624B\u6CD5\uFF0C\u901F\u5199\u80FD\u5E2E\u4F60\u56DE\u5FC6\u8D77\u5B83\u3002\u5982\u679C\u4F60\u662F\u7B2C\u4E00\u6B21\u63A5\u89E6\u5230\u8FD9\u4E2A\u91CD\u6784\u624B\u6CD5\uFF0C\u53EF\u80FD\u6700\u597D\u662F\u5148\u9605\u8BFB\u8303\u4F8B\u90E8\u5206\u3002\u6211\u8FD8\u7ED9\u6BCF\u4E2A\u91CD\u6784\u624B\u6CD5\u753B\u4E86\u4E00\u5E45\u5C0F\u56FE\u3002\u540C\u6837\uFF0C\u6211\u4E5F\u4E0D\u6307\u671B\u8FD9\u4E9B\u5C0F\u56FE\u80FD\u8BF4\u6E05\u91CD\u6784\u624B\u6CD5\u7684\u5185\u5BB9\uFF0C\u53EA\u662F\u63D0\u4F9B\u4E00\u70B9\u56FE\u50CF\u8BB0\u5FC6\u7684\u7EBF\u7D22\u3002</p><p>\u201C\u505A\u6CD5\u201D\u51FA\u81EA\u6211\u81EA\u5DF1\u7684\u7B14\u8BB0\u3002\u8FD9\u4E9B\u7B14\u8BB0\u662F\u4E3A\u4E86\u8BA9\u6211\u5728\u4E00\u6BB5\u65F6\u95F4\u4E0D\u505A\u67D0\u9879\u91CD\u6784\u4E4B\u540E\u8FD8\u80FD\u8BB0\u5F97\u600E\u4E48\u505A\u3002\u5B83\u4EEC\u4E5F\u9887\u4E3A\u7B80\u6D01\uFF0C\u901A\u5E38\u4E0D\u4F1A\u89E3\u91CA\u201C\u4E3A\u4EC0\u4E48\u8981\u8FD9\u4E48\u505A\u90A3\u4E48\u505A\u201D\u3002\u6211\u4F1A\u5728\u201C\u8303\u4F8B\u201D\u4E2D\u7ED9\u51FA\u66F4\u591A\u89E3\u91CA\u3002\u8FD9\u4E48\u4E00\u6765\uFF0C\u201C\u505A\u6CD5\u201D\u5C31\u6210\u4E86\u7B80\u77ED\u7684\u7B14\u8BB0\u3002\u5982\u679C\u4F60\u77E5\u9053\u8BE5\u4F7F\u7528\u54EA\u4E2A\u91CD\u6784\uFF0C\u4F46\u8BB0\u4E0D\u6E05\u5177\u4F53\u6B65\u9AA4\uFF0C\u53EF\u4EE5\u53C2\u8003\u201C\u505A\u6CD5\u201D\u90E8\u5206\uFF08\u81F3\u5C11\u6211\u662F\u8FD9\u4E48\u4F7F\u7528\u5B83\u4EEC\u7684\uFF09\uFF1B\u5982\u679C\u4F60\u521D\u6B21\u4F7F\u7528\u67D0\u4E2A\u91CD\u6784\uFF0C\u53EF\u80FD\u53EA\u53C2\u8003\u201C\u505A\u6CD5\u201D\u8FD8\u4E0D\u591F\uFF0C\u4F60\u8FD8\u9700\u8981\u9605\u8BFB\u201C\u8303\u4F8B\u201D\u3002</p><p>\u64B0\u5199\u201C\u505A\u6CD5\u201D\u7684\u65F6\u5019\uFF0C\u6211\u5C3D\u91CF\u5C06\u91CD\u6784\u7684\u6BCF\u4E2A\u6B65\u9AA4\u62C6\u5F97\u5C3D\u53EF\u80FD\u5C0F\u3002\u6211\u5F3A\u8C03\u5B89\u5168\u7684\u91CD\u6784\u65B9\u5F0F\uFF0C\u6240\u4EE5\u5E94\u8BE5\u91C7\u7528\u975E\u5E38\u5C0F\u7684\u6B65\u9AA4\uFF0C\u5E76\u4E14\u5728\u6BCF\u4E2A\u6B65\u9AA4\u4E4B\u540E\u8FDB\u884C\u6D4B\u8BD5\u3002\u771F\u6B63\u5DE5\u4F5C\u65F6\uFF0C\u6211\u901A\u5E38\u4F1A\u91C7\u7528\u6BD4\u8FD9\u91CC\u4ECB\u7ECD\u7684\u201C\u5A74\u513F\u5B66\u6B65\u201D\u7A0D\u5927\u4E9B\u7684\u6B65\u9AA4\uFF0C\u7136\u800C\u4E00\u65E6\u51FA\u95EE\u9898\uFF0C\u6211\u5C31\u4F1A\u64A4\u9500\u4E0A\u4E00\u6B65\uFF0C\u6362\u7528\u6BD4\u8F83\u5C0F\u7684\u6B65\u9AA4\u3002\u8FD9\u4E9B\u6B65\u9AA4\u8FD8\u5305\u542B\u4E00\u4E9B\u7279\u6B8A\u60C5\u51B5\u7684\u53C2\u8003\uFF0C\u6240\u4EE5\u5B83\u4EEC\u4E5F\u6709\u68C0\u67E5\u6E05\u5355\u7684\u4F5C\u7528\u3002\u6211\u81EA\u5DF1\u7ECF\u5E38\u5FD8\u6389\u8FD9\u4E9B\u8BE5\u505A\u7684\u4E8B\u60C5\u3002</p><p>\u7EDD\u5927\u591A\u6570\u65F6\u5019\u6211\u53EA\u5217\u51FA\u4E86\u91CD\u6784\u7684\u4E00\u5957\u505A\u6CD5\uFF0C\u4F46\u5176\u5B9E\u4E00\u4E2A\u91CD\u6784\u5E76\u975E\u53EA\u6709\u4E00\u5957\u505A\u6CD5\u3002\u6211\u5728\u672C\u4E66\u4E2D\u9009\u62E9\u4ECB\u7ECD\u8FD9\u4E9B\u505A\u6CD5\uFF0C\u56E0\u4E3A\u5B83\u4EEC\u5927\u591A\u6570\u65F6\u5019\u90FD\u7BA1\u7528\u3002\u7B49\u4F60\u7ECF\u8FC7\u7EC3\u4E60\u83B7\u5F97\u66F4\u591A\u91CD\u6784\u7ECF\u9A8C\uFF0C\u4F60\u53EF\u80FD\u4F1A\u8C03\u6574\u91CD\u6784\u7684\u505A\u6CD5\uFF0C\u90A3\u5B8C\u5168\u6CA1\u95EE\u9898\u3002\u53EA\u8981\u7262\u8BB0\u4E00\u70B9\uFF1A\u5C0F\u6B65\u524D\u8FDB\uFF0C\u60C5\u51B5\u8D8A\u590D\u6742\uFF0C\u6B65\u5B50\u5C31\u8981\u8D8A\u5C0F\u3002</p><p>\u201C\u8303\u4F8B\u201D\u50CF\u662F\u7B80\u5355\u800C\u6709\u8DA3\u7684\u6559\u79D1\u4E66\u3002\u6211\u4F7F\u7528\u8FD9\u4E9B\u8303\u4F8B\u662F\u4E3A\u4E86\u5E2E\u52A9\u89E3\u91CA\u91CD\u6784\u7684\u57FA\u672C\u8981\u7D20\uFF0C\u6700\u5927\u9650\u5EA6\u5730\u907F\u514D\u5176\u4ED6\u679D\u8282\uFF0C\u6240\u4EE5\u6211\u5E0C\u671B\u4F60\u80FD\u539F\u8C05\u5176\u4E2D\u7684\u7B80\u5316\u5DE5\u4F5C\uFF08\u5B83\u4EEC\u5F53\u7136\u4E0D\u662F\u4F18\u79C0\u7684\u4E1A\u52A1\u5EFA\u6A21\u4F8B\u5B50\uFF09\u3002\u4E0D\u8FC7\u6211\u6562\u80AF\u5B9A\uFF0C\u4F60\u4E00\u5B9A\u80FD\u5728\u90A3\u4E9B\u66F4\u590D\u6742\u7684\u60C5\u51B5\u4E2D\u4F7F\u7528\u5B83\u4EEC\u3002\u67D0\u4E9B\u5341\u5206\u7B80\u5355\u7684\u91CD\u6784\u5E72\u8106\u6CA1\u6709\u8303\u4F8B\uFF0C\u56E0\u4E3A\u6211\u89C9\u5F97\u4E3A\u5B83\u4EEC\u52A0\u4E0A\u4E00\u4E2A\u8303\u4F8B\u4E0D\u4F1A\u6709\u591A\u5927\u610F\u4E49\u3002</p><p>\u66F4\u660E\u786E\u5730\u8BF4\uFF0C\u52A0\u4E0A\u8303\u4F8B\u4EC5\u4EC5\u662F\u4E3A\u4E86\u9610\u91CA\u5F53\u65F6\u8BA8\u8BBA\u7684\u91CD\u6784\u624B\u6CD5\u3002\u901A\u5E38\u90A3\u4E9B\u4EE3\u7801\u6700\u7EC8\u4ECD\u6709\u5176\u4ED6\u95EE\u9898\uFF0C\u4F46\u4FEE\u6B63\u90A3\u4E9B\u95EE\u9898\u9700\u8981\u7528\u5230\u5176\u4ED6\u91CD\u6784\u624B\u6CD5\u3002\u67D0\u4E9B\u60C5\u51B5\u4E0B\u6570\u4E2A\u91CD\u6784\u7ECF\u5E38\u88AB\u4E00\u5E76\u8FD0\u7528\uFF0C\u8FD9\u65F6\u5019\u6211\u4F1A\u628A\u8303\u4F8B\u5E26\u5230\u53E6\u4E00\u4E2A\u91CD\u6784\u4E2D\u7EE7\u7EED\u4F7F\u7528\u3002\u5927\u90E8\u5206\u65F6\u5019\uFF0C\u4E00\u4E2A\u8303\u4F8B\u53EA\u4E3A\u4E00\u9879\u91CD\u6784\u800C\u8BBE\u8BA1\uFF0C\u8FD9\u4E48\u505A\u662F\u4E3A\u4E86\u8BA9\u6BCF\u4E00\u9879\u91CD\u6784\u624B\u6CD5\u81EA\u6210\u4E00\u4F53\uFF0C\u56E0\u4E3A\u8FD9\u4EFD\u91CD\u6784\u540D\u5F55\u7684\u9996\u8981\u76EE\u7684\u8FD8\u662F\u4F5C\u4E3A\u53C2\u8003\u5DE5\u5177\u3002</p><p>\u4FEE\u6539\u540E\u7684\u4EE3\u7801\u53EF\u80FD\u88AB\u57CB\u6CA1\u5728\u672A\u4FEE\u6539\u7684\u4EE3\u7801\u4E2D\uFF0C\u96BE\u4EE5\u4E00\u773C\u770B\u51FA\uFF0C\u6240\u4EE5\u6211\u4F7F\u7528\u4E0D\u540C\u7684\u989C\u8272\u7A81\u51FA\u663E\u793A\u4FEE\u6539\u8FC7\u7684\u4EE3\u7801\u3002\u4F46\u6211\u5E76\u6CA1\u6709\u7A81\u51FA\u663E\u793A\u6240\u6709\u4FEE\u6539\u8FC7\u7684\u4EE3\u7801\uFF0C\u56E0\u4E3A\u4E00\u65E6\u4FEE\u6539\u8FC7\u7684\u4EE3\u7801\u592A\u591A\uFF0C\u5168\u90FD\u7A81\u51FA\u663E\u793A\u53CD\u800C\u4E0D\u80FD\u7A81\u663E\u91CD\u70B9\u3002</p><h2 id="_5-2-\u6311\u9009\u91CD\u6784\u7684\u4F9D\u636E" tabindex="-1"><a class="header-anchor" href="#_5-2-\u6311\u9009\u91CD\u6784\u7684\u4F9D\u636E" aria-hidden="true">#</a> 5.2 \u6311\u9009\u91CD\u6784\u7684\u4F9D\u636E</h2><p>\u8FD9\u4E0D\u662F\u4E00\u4EFD\u5DE8\u7EC6\u9761\u9057\u7684\u91CD\u6784\u540D\u5F55\u3002\u6211\u53EA\u662F\u8BA4\u4E3A\u8FD9\u4E9B\u91CD\u6784\u624B\u6CD5\u6700\u503C\u5F97\u88AB\u8BB0\u5F55\u4E0B\u6765\u3002\u4E4B\u6240\u4EE5\u8BF4\u5B83\u4EEC\u201C\u6700\u503C\u5F97\u201D\uFF0C\u56E0\u4E3A\u8FD9\u4E9B\u90FD\u662F\u5F88\u5E38\u7528\u7684\u91CD\u6784\u624B\u6CD5\uFF0C\u5E76\u4E14\u503C\u5F97\u7ED9\u5B83\u4EEC\u547D\u540D\u548C\u8BE6\u7EC6\u7684\u4ECB\u7ECD\uFF1A\u5176\u4E2D\u4E00\u4E9B\u505A\u6CD5\u5F88\u6709\u610F\u601D\uFF0C\u80FD\u5E2E\u52A9\u8BFB\u8005\u63D0\u9AD8\u6574\u4F53\u91CD\u6784\u6280\u80FD\u6C34\u5E73\uFF0C\u53E6\u5916\u4E00\u4E9B\u5219\u5BF9\u4E8E\u4EE3\u7801\u8BBE\u8BA1\u8D28\u91CF\u7684\u63D0\u5347\u6548\u679C\u663E\u8457\u3002</p><p>\u6709\u4E9B\u91CD\u6784\u6CA1\u6709\u8FDB\u5165\u8FD9\u4EFD\u540D\u5F55\uFF0C\u56E0\u4E3A\u5B83\u4EEC\u592A\u5C0F\u3001\u592A\u7B80\u5355\uFF0C\u6211\u89C9\u5F97\u6CA1\u5FC5\u8981\u591A\u52A0\u8D58\u8FF0\u3002\u4F8B\u5982\uFF0C\u5728\u64B0\u5199\u7B2C 1 \u7248\u65F6\u6211\u5C31\u66FE\u7ECF\u8003\u8651\u8FC7\u79FB\u52A8\u8BED\u53E5\uFF08223\uFF09\uFF0C\u8FD9\u4E2A\u91CD\u6784\u6211\u7ECF\u5E38\u4F7F\u7528\uFF0C\u4F46\u6211\u89C9\u5F97\u6CA1\u5FC5\u8981\u5C06\u5B83\u653E\u8FDB\u540D\u5F55\u91CC\uFF08\u663E\u7136\u6211\u5728\u5199\u7B2C 2 \u7248\u7684\u65F6\u5019\u6539\u53D8\u4E86\u60F3\u6CD5\uFF09\u3002\u4EE5\u540E\u4E5F\u8BB8\u8FD8\u6709\u7C7B\u4F3C\u8FD9\u6837\u7684\u91CD\u6784\u4F1A\u88AB\u52A0\u8FDB\u4E66\u91CC\uFF0C\u4E0D\u8FC7\u90A3\u8981\u770B\u6211\u6295\u5165\u591A\u5C11\u7CBE\u529B\u5728\u65B0\u589E\u91CD\u6784\u4E0A\u4E86\u3002</p><p>\u8FD8\u6709\u4E00\u4E9B\u6CA1\u6709\u8FDB\u5165\u540D\u5F55\u7684\u91CD\u6784\uFF0C\u8981\u4E48\u662F\u6211\u7528\u5F97\u5F88\u5C11\uFF0C\u8981\u4E48\u662F\u4E0E\u5176\u4ED6\u91CD\u6784\u975E\u5E38\u76F8\u4F3C\u3002\u672C\u4E66\u4E2D\u7684\u6BCF\u4E2A\u91CD\u6784\uFF0C\u903B\u8F91\u4E0A\u6765\u8BF4\uFF0C\u90FD\u6709\u4E00\u4E2A\u53CD\u5411\u7684\u91CD\u6784\u3002\u4F46\u6211\u5E76\u6CA1\u6709\u628A\u6240\u6709\u53CD\u5411\u91CD\u6784\u90FD\u5199\u4E0B\u6765\uFF0C\u56E0\u4E3A\u6211\u53D1\u73B0\u5F88\u591A\u53CD\u5411\u91CD\u6784\u6CA1\u592A\u5927\u610F\u601D\u3002\u4F8B\u5982\uFF0C\u5C01\u88C5\u53D8\u91CF\uFF08132\uFF09\u662F\u4E00\u4E2A\u5E38\u7528\u53C8\u597D\u7528\u7684\u91CD\u6784\uFF0C\u4F46\u5B83\u7684\u53CD\u5411\u91CD\u6784\u6211\u51E0\u4E4E\u4ECE\u6765\u4E0D\u4F1A\u505A\uFF08\u800C\u4E14\u5C31\u7B97\u8981\u505A\u4E5F\u975E\u5E38\u7B80\u5355\uFF09\uFF0C\u6240\u4EE5\u6211\u89C9\u5F97\u6CA1\u5FC5\u8981\u5C06\u8FD9\u4E2A\u53CD\u5411\u91CD\u6784\u653E\u8FDB\u540D\u5F55\u3002</p>',16);function r(h,t){return p}var d=a(i,[["render",r]]);export{d as default};
